import React, { Component } from "react";
import { Link } from "react-router-dom";
import ReactHowler from "react-howler";

class Rain extends Component {
  render() {
    return (
      <div className="container">
        <div className="texts">
          <Link to="/" style={{ textDecoration: "none" }}>
            <div>
              <h1 className="midnight">Midnight Chill</h1>
            </div>
          </Link>
          <div>
            <p className="choose">Lo Fi</p>
          </div>
        </div>
        <div className="gifs">
          <div className="gif-10">
            <img className="gif-1" src={require("../gifs/1.gif")} alt="rain" />
            <audio className="audio-element">
              <source src="https://www.mboxdrive.com/sweet.mp3" />
            </audio>
            <p>Lofi</p>
            <button onClick={this.playAudio}>Play</button>
            <button style={{ display: "none" }} onClick={this.stopAudio}>
              Pause
            </button>
          </div>
        </div>
      </div>
    );
  }
  playAudio() {
    const audioPlay = document.getElementsByClassName("audio-element")[0];
    audioPlay.play();
  }
  stopAudio() {
    const audioPause = document.getElementsByClassName("audio-element")[0];
    audioPause.pause();
    audioPause.style.display.block;
  }
}
export default Rain;
