import React from "react";
import { Link } from "react-router-dom";
const { Howl, Howler } = require("howler");
//soundcloud.com/steezyasfuck/beats-to-sleep-to-lofi-hip-hop-mix

const play = function handleCLick(e) {
  e.preventDefault();
  var sound = new Howl({
    src: [
      "https:soundcloud.com/steezyasfuck/beats-to-sleep-to-lofi-hip-hop-mix",
    ],
  });
  sound.play();
  alert("clicked!");
};

function Rain() {
  return (
    <div className="container">
      <div className="texts">
        <Link to="/" style={{ textDecoration: "none" }}>
          <div>
            <h1 className="midnight">Midnight Chill</h1>
          </div>
        </Link>
        <div>
          <p className="choose">Lo Fi</p>
        </div>
      </div>
      <div className="gifs">
        <div className="gif-10">
          <img className="gif-1" src={require("../gifs/1.gif")} alt="rain" />

          <p>Lofi</p>

          <button onClick={play()}>Click me</button>
        </div>
      </div>
    </div>
  );
}
export default Rain;
