import React, { Component } from "react";

export class Buttons extends Component {
  render() {
    return (
      <div>
        <button style={{ buttonPlay }} onClick={this.playAudio}>
          Play
        </button>
        <button onClick={this.stopAudio}>Pause</button>
      </div>
    );
    const buttonPlay = (
      <style>color: "white", width: '100px', heigth: '50px'</style>
    );
  }
  playAudio() {
    const audioPlay = document.getElementsByClassName("audio-element")[0];
    audioPlay.play();
  }
  stopAudio() {
    const audioPause = document.getElementsByClassName("audio-element")[0];
    audioPause.pause();
  }
}

export default Buttons;
